PREFIX time-fn: <https://w3id.org/time-fn/>
PREFIX dbpo: <http://dbpedia.org/ontology/>
PREFIX rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#>
PREFIX schema: <http://schema.org/>

SELECT ?publication ?title ?type ?pubDate ?birthDate
WHERE {
  SERVICE <https://dbpedia.org/sparql> {
    <http://dbpedia.org/resource/Bob_Evnen> a dbpo:Person ;
                 dbpo:birthDate ?birthDate.
  }
  SERVICE <https://ugent-biblio.smessie.com/sparql> {
  	?publication a ?type ;
   	             schema:dateCreated ?pubDate ;
                 schema:name ?title.
  }
  FILTER(?birthDate = ?pubDate).
}

